// IONSLIDES FOR TABS?
// ionChange	Emitted when the checked property has changed.

import React, { useState } from 'react';
import { IonAlert, IonButton, IonIcon, IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonCheckbox, IonList, IonItem, IonLabel, IonItemDivider  } from '@ionic/react';
import { SMS } from '@ionic-native/sms';
import { Contacts, Contact, ContactFindOptions, ContactFieldType } from '@ionic-native/contacts';
import ExploreContainer from '../components/ExploreContainer';
import './Tab3.css';
import { scryRenderedDOMComponentsWithClass } from 'react-dom/test-utils';
import { isConstructorDeclaration } from 'typescript';
//import './ContactTracing'


export class ContactTracing {
  constructor( private contacts:Contacts) {}

  sendMessage(){
    this.loadContacts();
    this.sendSms();

  }

  // onSuccess(contacts:Contact[]){

  // }

  loadContacts(){
    var myContacts:Promise<Contact[]>;
    let options = new ContactFindOptions();
    options.filter= "";
    options.multiple= true;
    //options.hasPhoneNumber= true; //Android only
    //options.desiredFields = 
    var fields:ContactFieldType[] = ['displayName', 'phoneNumbers'];

    myContacts = Contacts.prototype.find(fields, options);

    //Need to create a display to show contacts

    alert("Contacts Loaded!");
  }

  sendSms(){

    var message:String = "";

    alert(symptomList[0].isChecked);
    message += "IF EXPERIENCING SEEK IMMEDIATE MEDICAL ATTENTION!\n";
    for(var index in emergencyList){
      if(emergencyList[index].isChecked){
        message += emergencyList[index].val + "\n";
      }
    }

    message += "Non-Emergency Symptoms:\n";
    for(var index in symptomList){
      if(symptomList[index].isChecked){
        message += symptomList[index].val + "\n";
      }
    }

    alert("Symptoms List \n" + message);
    SMS.send('5167120628', "test sms");
    alert("sendSms was called!");
  }

  checkSymptom(i:int){

  }

}



const symptomList = [
  {val: "Cough", isChecked: false},
  {val: "Fever or chills", isChecked: false},
  {val: "Shortness of breath or difficulty breathing", isChecked: false},
  {val: "Fatigue", isChecked: false},
  {val: "Muscle or body aches", isChecked: false},
  {val: "Headaches", isChecked: false},
  {val: "New loss of taste or smell", isChecked: false},
  {val: "Sore throat", isChecked: false},
  {val: "Congestion or runny nose", isChecked: false},
  {val: "Nausea or vomiting", isChecked: false},
  {val: "Diarrhea", isChecked: false}
]

const emergencyList = [
  {val: "Trouble breathing", isChecked: false},
  {val: "Persistent pain or pressure in chest", isChecked: false},
  {val: "New confusion", isChecked: false},
  {val: "Inability to wake or stay awake", isChecked: false},
  {val: "Bluish lips or face", isChecked: false}
]


const Tab3: React.FC = () => {
  const [checked, setChecked] = useState(false);
  const [showAlert1, setShowAlert1] = useState(false);
  const ct:ContactTracing = new ContactTracing(new Contacts());


  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Check Symptoms Experienced</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
      <IonList>
          <IonItemDivider>EMERGENCY WARNING SIGNS - IF EXPERIENCING SEEK IMMEDIATE MEDICAL ATTENTION</IonItemDivider>
          {emergencyList.map(({ val, isChecked }, i) => (
            <IonItem key={i}>
              <IonLabel>{val}</IonLabel>
              <IonCheckbox slot="end" value={val} checked={isChecked} />
            </IonItem>
          ))}
      </IonList>

      <IonList>
          <IonItemDivider>Symptoms exprienced 2-14 days after exposure</IonItemDivider>
          {symptomList.map(({ val, isChecked }, i) => (
            <IonItem key={i}>
              <IonLabel>{val}</IonLabel>
              <IonCheckbox slot="end" value={val} checked={isChecked} onClick={()=>ct.checkSymptom(i)} />

            </IonItem>
          ))}
      </IonList>

      <IonButton onClick={()=>ct.sendMessage()} expand="block" shape="round"  color="dark" fill="solid">
        Send Messages
      </IonButton>
      {/* <IonAlert
        isOpen={showAlert1}
        onDidDismiss={() => setShowAlert1(false)}
        cssClass='my-custom-class'
        header={'Test Alert'}
        subHeader={'Subtitle'}
        message={'function has executed.'}
        buttons={['OK']}
      /> */}

      </IonContent>

    </IonPage>
  );
};


export default Tab3;
