import { IonCheckbox, IonItem, IonTitle, IonAlert, IonBackdrop, IonContent, IonLabel, IonList, IonListHeader, IonToolbar, IonHeader, IonButton } from "@ionic/react";
import React from 'react'
import { Contact } from "@capacitor-community/contacts";


function check(i:number, list:boolean[]){
    if(i < list.length){
      list[i] = !(list[i]);
    }
  }

function select(c:Contact[],indexes:boolean[], selectedContacts:Contact[]){
    for (let i = 0; i < indexes.length; i++){
        if(indexes[i]){
            selectedContacts.push(c[i]);
        }
    }
}

const displayContacts = function(contacts:Contact[], selectedContacts:Contact[]) {
    const contactIndex:boolean[] = [];
    var backdrop:boolean = true;
    return(
        <IonContent fullscreen>
            <IonBackdrop visible={backdrop}>
                <IonHeader translucent>
                    <IonToolbar>
                        <IonTitle>
                            Contacts
                        </IonTitle>
                    </IonToolbar>
                </IonHeader>
                <IonList mode="ios" >
                    <IonListHeader>Send to: </IonListHeader>  
                    {contacts.map(( { displayName, phoneNumbers }, i) => (
                        <IonItem key={i}>
                            <IonLabel>
                                <h2>{displayName}</h2>
                                <h3>{phoneNumbers}</h3>
                            </IonLabel>
                            <IonCheckbox slot="end" value={displayName} checked={contactIndex[i]} onClick={()=>check(i, contactIndex)} />
                        </IonItem>
                    ))}
                        {/* Need to implement checkboxes too
                        <h2>
                            Contact Name here
                        </h2>
                        <h3>
                            Contact Phone number here
                        </h3>
                        <p>
                            Extra if needed
                        </p> 
                        */}
                    <IonItem>
                        <IonButton onClick={()=>{select(contacts, contactIndex, selectedContacts),backdrop=false}} expand="block" shape="round"  color="dark" fill="solid"> </IonButton>
                    </IonItem>
                </IonList>
            </IonBackdrop>
        </IonContent>
    )
};
export default displayContacts;
